  $(document).ready(function(){
  /* この中に処理を記述する */

  /* header */
    /*  */
    /* マウスオーバーイベント */
    $(".header-image").mouseover('over',function(){
      $(this).animate(
        {
          "opacity":".5"
        },
        {
          duration:200,
          easing:"swing"
        })
    });
    $(".header-image").mouseout('over',function(){
      $(this).animate(
        {
          "opacity":"1"
        },
        {
          duration:200,
          easing:"swing"
        })
    });

    /* fadeIn-fromTop- */
    $(function(){
        $(window).ready(function(){
          $('.fadeInTop').each(function(){
            var targetElement = $(this).offset().top;
            var scroll = $(window).scrollTop();
            var windowHeight = $(window).height();

            if(scroll > targetElement - windowHeight + 200){
                $(this).css('opacity','1');
                $(this).css('transform','translateY(0)');
            }
          });
        });
    });

    /* fadeIn-fromBottom- */
    $(function(){
        $(window).ready(function(){
          $('.fadeInBottom').each(function(){
            var targetElement = $(this).offset().top;
            var scroll = $(window).scrollTop();
            var windowHeight = $(window).height();

            if(scroll > targetElement - windowHeight + 200){
                $(this).css('opacity','1');
                $(this).css('transform','translateY(0)');
            }
          });
        });
    });

    /* fadeIn-fromLeft- */
    $(function(){
        $(window).ready(function(){
          $('.fadeInLeft').each(function(){
            var targetElement = $(this).offset().top;
            var scroll = $(window).scrollTop();
            var windowHeight = $(window).height();

            if(scroll > targetElement - windowHeight + 200){
                $(this).css('opacity','1');
                $(this).css('transform','translateY(0)');
            }
          });
        });
    });

    /* scrollWatch */
    $(function () {
      $('.box').each(function () {
        var $win = $(window),
            $winH = $win.height(),
            $connect = $(this),
            position = $connect.offset().top,
            current = 0,
            scroll;

        $win.on('load scroll', function () {
          scroll = $win.scrollTop();
          current = (1 - (position - scroll) / $winH) * 2 * 100;
          if (current > 99.9) {
            current = 100;
          }
          if (scroll > position - $winH) {
            $connect.css({width: current + '%'});
          }
        });
      });
    });

    /* nav */
    $(function(){
      var nav = $('.nav');
      var navHeight = nav.outerheight(true);
      var navOffset = nav.offset().top;

      $(window).scroll(function(){
        if($(this.scrollTop() > navOffset))
        {
          nav.css({
            'position' : 'fixed',
            'top' : 0,
            'width' : '100%'
          });
        }else{
          nav.css({
            'position' : 'relative',
          });
        }
      });

    });


  });
